    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        FileExplorer fileEx = new FileExplorer(this, true);
        fileEx.setVisible(true);
        try {
            imageData = Files.readAllBytes(Paths.get(FileExplorer.file_.getAbsolutePath()));
        } catch (IOException ex) {
            JOptionPane.showMessageDialog(null, ex.getMessage());
        }

        fileEx.dispose();
        if (imageData != null) {
            Image img = new ImageIcon(imageData).getImage();
            jLabel6.setIcon(new ImageIcon(img.getScaledInstance(120, 120, Image.SCALE_DEFAULT)));
        }
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        String gender = "";
        if (radMale.isSelected()) {
            gender = "Male";
        } else if (radFemale.isSelected()) {
            gender = "Female";
        }
        try {
            PreparedStatement ps = null;
            String sql = "insert into students(LastName, FirstName, OtherName,Age, Gender, "
                    + "Username, Password, Country, StateOfOrigin,PhoneNumber, "
                    + "DateOfBirth, Class, Photo)values(?,?,?,?,?,?,?,?,?,?,?,?,?)";
            ps = StudyDoctorDatabaseManager.connect().prepareStatement(sql);
            ps.setString(1, txtLastName.getText());
            ps.setString(2, txtFirstName.getText());
            ps.setString(3, txtOtherName.getText());
            ps.setInt(4, (int) spnAge.getValue());
            ps.setString(5, gender);
            ps.setString(6, txtUserName.getText());
            String user = txtUserName.getText();
            ps.setString(7, Arrays.toString(txtPassword.getPassword()));
            ps.setString(8, (String) cbxCountry.getSelectedItem());
            ps.setString(9, (String) cbxStateOfOrigin.getSelectedItem());
            ps.setString(10, txtPhoneNumber.getText());
            ps.setString(11, txtDateOfBirth.getText());
            ps.setString(12, (String) cbxClass.getSelectedItem());
            ps.setBytes(13, imageData);
            int count = ps.executeUpdate();
            if (count != 0) {
                JOptionPane.showMessageDialog(null, "Login with your username: "+user, "Good News", JOptionPane.INFORMATION_MESSAGE);
                clearFields();
                this.dispose();
                StudyDoctorDatabaseManager.disconnect();
                new Login().setVisible(true);
            }

        } catch (ClassNotFoundException | SQLException ex) {
            JOptionPane.showMessageDialog(null, ex.getMessage(), "AN ERROR OCCURED", HEIGHT);
        }
    }//GEN-LAST:event_jButton3ActionPerformed

